      var astralImage:[String] = ["aries.jpg", "taurus.jpg", "gemini.jpg", "cancer.jpg", "leo.jpg", "virgo.jpg", "libra.jpg", "scorpio.jpg", "sagittarius.jpg", "capricorn.jpg", "aquarius.jpg", "pisces.jpg"]
    
    var astralUnse = [[String:String]]()
    var todayUnse = [[String:String]]()
    
    init()
    {
        let calendar = Calendar.current
        let date = Date()
        let currentYear = calendar.component(.year, from: date)
        let currentMonth = calendar.component(.month, from: date)
        let currentDay = calendar.component(.day, from: date)
        
        for index in 0...astralName.count-1 {
             astralUnse.append( [ "astralName":astralName[index], "astralString":astralString[(index + currentDay + (ViewController.mUser!.birthDay)) % astralString.count], "astralImage":astralImage[index] ] )
        }
        
        var pseudoRandomNumber = (currentYear + currentMonth + currentDay + ViewController.mUser!.birthYear + ViewController.mUser!.birthMonth + ViewController.mUser!.birthDay)
        /*운세를 날짜와 개인 생년월일을 시드값으로 모드연산 수도랜덤 공식을 통해 랜덤으로 append*/
        
        for index in 0...zodiacName.count - 1{
            pseudoRandomNumber = (pseudoRandomNumber + index) % zodiacName.count
            
            todayUnse.append( ["zodiacString":zodiacString[pseudoRandomNumber ],"zodiacName":zodiacName[index],"zodiacImage":zodiacImage[index]])
        }
    }

